/* ======= helpers ====== */

/* tooltip theme
   https://atomiks.github.io/tippyjs/#creating-themes
*/

.tippy-tooltip.outstanding-theme {
    background-color: DarkOrange;
    padding-left: 2rem;
    padding-right: 2rem;
    line-height: 3rem;
    font-size: 2rem;
    border-radius: 0.5rem;
    color: var(--color-lightest);
}

.tippy-popper[x-placement^=bottom] .tippy-tooltip.outstanding-theme .tippy-arrow {
    border-bottom-color: DarkOrange;
}

/* --- loader ---
   src: https://www.w3schools.com/howto/howto_css_loader.asp
*/
.loader {
    border: 16px solid #f3f3f3;
    border-radius: 50%;
    border-top: 16px solid #3498db;
    width: 120px;
    height: 120px;
    -webkit-animation: spin 2s linear infinite; /* Safari */
    animation: spin 2s linear infinite;
    display: none;
}

.loader-big {
    width: 120px;
    height: 120px;
}

.loader-small {
    width: 60px;
    height: 60px;
}

.loader-visible {
    display: block;
}

/* Safari */
@-webkit-keyframes spin {
    0% { -webkit-transform: rotate(0deg); }
    100% { -webkit-transform: rotate(360deg); }
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* ======== City List Containers ========= */

.city-container {
    position: absolute;
    background-color: #fff;
    border: 1px solid #999;
    display: none;
    width: 100%;
    margin-top: 3rem;
}

.city-container-visible {
    display: block;
}

.city-list-element {
    padding: 0.5rem 0.5rem 0.25rem 0.5rem;
    font-size: 1.5rem;
    cursor: default;
}

.city-list-element > i {
    font-size: 1.5rem;
}

.city-list-element:hover {
    background-color: #84a5ff;
}

input[type=radio].city-list-radio {
    position: absolute;
    overflow: hidden;
    clip: rect(0 0 0 0);
    height: 1px;
    width: 1px;
    margin: -1px;
    padding: 0;
    border: 0;
}

input[type=radio].city-list-radio:checked + .city-list-element {
    background-color: #84a5ff; /* yellow */
}
